import React, { Component } from 'react';
import './App.css';
import Clarifai from 'clarifai';
import Particles from 'react-particles-js';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import Rank from './components/Rank/Rank';
import Register from './components/Register/Register';
import SignIn from './components/Signin/SignIn';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';


//import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
const option={
  particles: {
    number: {
      value:50,
      density: {
        enable: true,
        value_area : 800
      }
    }
  }
};




 const initialState={


    input:'',
    imageUrl:'',
    box:{},
    route:'signin',
    isSignedIn:false,
    user:{
      id:'',
      name:'',
      email:'',
      entries:0,
      joined:'' 
  }


 };
class App extends Component{

  constructor(){
    super();
    this.state=initialState;

  }

  loadUser=(data)=>{

    this.setState({user:{
      id:data.id,
      name:data.name,
      email:data.email,
      entries:data.entries,
      joined:data.joined 
  }})
  }

  componentDidMount(){

    fetch('https://shielded-thicket-70648.herokuapp.com/').then(response=>{

      return response.json();
    }
    ).then(data=> console.log(data))
  }
  calculateFaceLocation=(data)=>{

    const clarifaiFace=data.outputs[0].data.regions[0].region_info.bounding_box;
    const image=document.getElementById('inputimage');
    const width=Number(image.width);
    const height=Number(image.height);

    console.log(width,height);

    return {

      leftCol: clarifaiFace.left_col*width,
      topRow:clarifaiFace.top_row*height,
      rightCol:width -(clarifaiFace.right_col*width),
      bottomRow:height - (clarifaiFace.bottom_row*height)
    }
  };

  displayFaceBox=(box)=>{
    console.log(box);
    this.setState({box:box})
  }
  onInputChange= (event)=>{

    console.log(event.target.value
      );

      this.setState({input:event.target.value})
  }
  onButtonSubmit=()=>{
    this.setState({imageUrl:this.state.input})
    console.log("click");
    fetch('https://shielded-thicket-70648.herokuapp.com/imageurl',{
          method:'post',
          headers:{
            'Content-Type':'application/json',
            'Accept': 'application/json'
          },
          body:JSON.stringify({
              input:this.state.input

          })

        }).then(
          (response)=>response.json()).then(
    (response)=> {

      if(response){
        fetch('https://shielded-thicket-70648.herokuapp.com/image',{
          method:'put',
          headers:{
            'Content-Type':'application/json',
            'Accept': 'application/json'
          },
          body:JSON.stringify({
              id:this.state.user.id

          })

        }).then(response=>response.json())
        .then(count =>{

          this.setState(Object.assign(this.state.user,{entries:count}))
        })
        .catch(console.log)

      }
      console.log(response);
      this.displayFaceBox(this.calculateFaceLocation(response));
    }).catch((err) =>{
        console.log(err);
    })


  }

  onRouteChange=(route)=>{

    if(route==='signout'){
      this.setState(initialState);
    }else if(route==='home'){

      this.setState({isSignedIn:true});
    } 
    this.setState({route:route});
  }

  render(){
     const {isSignedIn,route,box,imageUrl,user}=this.state;
    return (
      <div className="App">

        <Particles className='particles'
                params={option}

              />

        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>

        { route==='home'?
         <div>
         <Logo/>
         <Rank name={this.state.user.name} entries={this.state.user.entries} />
          <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>

          <FaceRecognition box={box} imageUrl={imageUrl}/>
       </div>

            :(

              route==='signin'?
              <SignIn loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
              :<Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
            )

        }


      </div>
    );
  }

}

export default App;
